template:
{{ define "mymessage" }}{{ range $index, $firing := .Alerts.Firing }}
• {{ $firing.Labels.alertname }}: {{ template "value" . }}{{ end }}
  {{ if gt (len .Alerts.Firing) 0 -}}
    {{- range $index, $firing := .Alerts.Firing }}
————————————————————————
        {{ template "myalert" . }}
    {{ end -}}
  {{ end }}
  {{- if gt (len .Alerts.Resolved) 0 -}}
    {{- len .Alerts.Resolved }} resolved:
    {{- range $index, $resolved := .Alerts.Resolved }}
------------------------------------------------
————————————————————————————————————————————————
        {{ template "myalert" .}}
      {{ end }}
  {{ end }}
{{ end }}

{{ define "title" }}
  {{ $threshold := "Threshold" }}
  {{ if gt (len .Alerts.Firing) 1 }} {{ $threshold = "Thresholds" }} {{ end }}
  {{ len .Alerts.Firing }} {{ $threshold }} Breached
{{ end }}

{{ define "value" }}
  {{- or (.ValueString | reReplaceAll `.* value=` "" | reReplaceAll ` \]` "") "MISSING" -}} 
{{- end -}}

{{ define "myalert" }}
{{ reReplaceAll "localhost:3000" "grafana.audius.co" .GeneratorURL }}
{{ .Labels.alertname }}: {{ template "value" . }}
Team: {{ or (.Labels.team) "MISSING" }}
{{- if gt (len .Labels.mentions) 0 }} ({{ .Labels.mentions }}) {{ end }}
{{ .Annotations.summary }}
{{ end }}
{{- /* • Runbook: {{ .Annotations.runbook_url }} */ -}}
{{- /* • Silence alert: {{ reReplaceAll "localhost:3000" "grafana.audius.co" .SilenceURL }} */ -}}
